import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-editable-element',
  templateUrl: './editable-element.component.html',
  styleUrls: ['./editable-element.component.css']
})
export class EditableElementComponent {
  @Input() value: string | number ='';
  @Output() valueChange = new EventEmitter<string | number>();
  @Output() showInput = false;
}
<p>editable-element works!</p>
<ng-container>
  <span *ngIf="!showInput">{{value}}</span>
  <input *ngIf="showInput" class="text-xs ml-2 text-gray-500 border shadow-sm" type="text"
      [(ngModel)]="value">
  <button *ngIf="!showInput" class="text-xs ml-2 text-gray-500" (click)="showInput = true">edit</button>
  <button *ngIf="showInput" class="text-xs ml-2 text-gray-500" (click)="onEdit()">save</button>
</ng-container>
