import { Component, Input, OnInit } from '@angular/core';
import { Customer } from '../models/customer.model';

@Component({
  selector: 'app-customer-card',
  templateUrl: './customer-card.component.html',
  styleUrls: ['./customer-card.component.css']
})
export class CustomerCardComponent{
  @Input() customer? : Customer;

}
<div class="flex flex-col gap-6 p-4col-span-1 text-center drop-shadow-md hover:drop-shadow-2xl hover:scale-110 rounded-xl p-4 h-[15rem] justify-center min-w-fit max-w-fit  w-fit max-h-xl bg-white">
  <div>
    <img [src]= "customer?.image" class="mx-auto h-44 w-44">
    <h3 class="font-bold">{{customer?.firstName}} {{customer?.lastName}}</h3>
    <p>{{customer?.address}}</p>
  </div>
</div>
