    class="justify-items-center items-center grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-20 p-4  mx-auto h-screen md:h-screen min-w-max">
    <app-customer-card *ngFor="let customer of customers" [customer]="customer"></app-customer-card>
  </div>
</div>
