import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Customer } from '../models/customer.model';

@Component({
  selector: 'app-customer-card',
  templateUrl: './customer-card.component.html',
  styleUrls: ['./customer-card.component.css']
})
export class CustomerCardComponent implements OnInit{
  firstCharFirstName :string | undefined="";
  firstCharLastName:string | undefined="";
  ngOnInit(): void {
    this.firstCharFirstName = this.customer?.firstName[0].toUpperCase();
    this.firstCharLastName=this.customer?.lastName[0].toUpperCase();
  }
  @Input() customer? : Customer;
  @Input() isDeleteLoading = false;
  @Output() delete = new EventEmitter<Customer>();

  onDelete(){
    this.delete.emit(this.customer);
  }
}
<div
      class="flex flex-col gap-6 p-4col-span-1 text-center drop-shadow-md hover:drop-shadow-xl hover:scale-105 rounded-xl p-4 h-[25rem]  justify-center items-center min-w-fit max-w-fit  w-fit max-h-xl bg-white cursor-pointer">
  <div>
      <button [routerLink]="['update', customer?.id]"
      class="flex p-2.5 bg-slate rounded-xl hover:rounded-3xl hover:bg-indigo-600  transition-all duration-300 text-indigo-600 hover:text-white absolute right-3 top-3">
        <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
            <path stroke-linecap="round" stroke-linejoin="round" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
        </svg>
        </button>
        <button (click)="onDelete()" [disabled]="isDeleteLoading"
        class="flex p-2.5 bg-slate rounded-xl hover:rounded-3xl hover:bg-red-500  transition-all duration-300 text-red-600 hover:text-white absolute right-3 top-16">
          <svg xmlns="http://www.w3.org/2000/svg"  fill="currentColor" class="bi bi-trash3 h-6 w-6" viewBox="0 0 16 16">
            <path d="M6.5 1h3a.5.5 0 0 1 .5.5v1H6v-1a.5.5 0 0 1 .5-.5ZM11 2.5v-1A1.5 1.5 0 0 0 9.5 0h-3A1.5 1.5 0 0 0 5 1.5v1H2.506a.58.58 0 0 0-.01 0H1.5a.5.5 0 0 0 0 1h.538l.853 10.66A2 2 0 0 0 4.885 16h6.23a2 2 0 0 0 1.994-1.84l.853-10.66h.538a.5.5 0 0 0 0-1h-.995a.59.59 0 0 0-.01 0H11Zm1.958 1-.846 10.58a1 1 0 0 1-.997.92h-6.23a1 1 0 0 1-.997-.92L3.042 3.5h9.916Zm-7.487 1a.5.5 0 0 1 .528.47l.5 8.5a.5.5 0 0 1-.998.06L5 5.03a.5.5 0 0 1 .47-.53Zm5.058 0a.5.5 0 0 1 .47.53l-.5 8.5a.5.5 0 1 1-.998-.06l.5-8.5a.5.5 0 0 1 .528-.47ZM8 4.5a.5.5 0 0 1 .5.5v8.5a.5.5 0 0 1-1 0V5a.5.5 0 0 1 .5-.5Z"/>
          </svg>
        </button>
        <div [routerLink]="['details', customer?.id]">
          <div *ngIf="customer?.image ==''" class="mx-auto h-44 w-44 rounded-full bg-red-300 flex justify-center items-center" [ngClass]="{'bg-blue-500': customer?.gender =='Male', 'bg-red-300 ':customer?.gender =='Female' }">
            <h1 class="text-5xl">{{firstCharFirstName}}{{firstCharLastName}}</h1>
          </div>
          <img [src]= "customer?.image" class="mx-auto h-44 w-44 rounded-full" *ngIf="customer?.image!=''">
          <h3 class="font-bold mt-4">{{customer?.firstName}} {{customer?.lastName}}</h3>
          <p>{{customer?.address}}</p>
          <div class="px-28 py-2">
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-envelope-at" viewBox="0 0 16 16">
              <path d="M2 2a2 2 0 0 0-2 2v8.01A2 2 0 0 0 2 14h5.5a.5.5 0 0 0 0-1H2a1 1 0 0 1-.966-.741l5.64-3.471L8 9.583l7-4.2V8.5a.5.5 0 0 0 1 0V4a2 2 0 0 0-2-2H2Zm3.708 6.208L1 11.105V5.383l4.708 2.825ZM1 4.217V4a1 1 0 0 1 1-1h12a1 1 0 0 1 1 1v.217l-7 4.2-7-4.2Z"/>
              <path d="M14.247 14.269c1.01 0 1.587-.857 1.587-2.025v-.21C15.834 10.43 14.64 9 12.52 9h-.035C10.42 9 9 10.36 9 12.432v.214C9 14.82 10.438 16 12.358 16h.044c.594 0 1.018-.074 1.237-.175v-.73c-.245.11-.673.18-1.18.18h-.044c-1.334 0-2.571-.788-2.571-2.655v-.157c0-1.657 1.058-2.724 2.64-2.724h.04c1.535 0 2.484 1.05 2.484 2.326v.118c0 .975-.324 1.39-.639 1.39-.232 0-.41-.148-.41-.42v-2.19h-.906v.569h-.03c-.084-.298-.368-.63-.954-.63-.778 0-1.259.555-1.259 1.4v.528c0 .892.49 1.434 1.26 1.434.471 0 .896-.227 1.014-.643h.043c.118.42.617.648 1.12.648Zm-2.453-1.588v-.227c0-.546.227-.791.573-.791.297 0 .572.192.572.708v.367c0 .573-.253.744-.564.744-.354 0-.581-.215-.581-.8Z"/>
            </svg>

          </div>
          <p class="">{{customer?.email}}</p>
        </div>
  </div>
</div>
