  <div class="items-center justify-center  p-10">

    <div><h1>Custmers</h1></div>
    <hr>
    <div class="mt-3">
      {{NumberOfCustmers}}
    </div>
  </div>

</div>
<div  class="w-full max-w-sm mx-auto bg-white border cursor-pointer hover:scale-95 flex justify-center items-center font-bold text-center border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
  <div class=" items-center  p-10">

    <div>Total Balance</div>
    <hr>
    <div class="mt-3">{{TotaleBalance |currency }}</div>
  </div>

</div>

