import { Component, OnInit } from '@angular/core';
import { debounceTime, distinctUntilChanged, Subject } from 'rxjs';
import { CustomersService} from '../customers.service.';
import { Customer } from '../models/customer.model';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit{
  showMenu = false;
  customers : Customer[] = [];
  searchQuery = '';
  searchQuerySubject = new Subject<string>();
  //isDeleteLoading: any[] = [];
  constructor(private customerService :CustomersService){
    this.searchQuerySubject
    .pipe(debounceTime(500), distinctUntilChanged())
    .subscribe((query: string) => {
      this.search(query);
    });
  }
  ngOnInit(): void {

  }
  toggleNavbar(){
    this.showMenu = !this.showMenu;
  }
  search(query: string) {
    this.customerService.search(query).subscribe((customers) => {
      this.customers = customers;
    });
  }
  onQuery(event: any) {
    this.searchQuerySubject.next(event.target.value);
  }
  // private setIsLoading(customer: Customer, isLoading: boolean) {
  //   this.isDeleteLoading = this.isDeleteLoading.map((p) => {
  //     if (p.id === customer.id) {
  //       return { ...p, isLoading };
  //     }
  //     return p;
  //   });
  // }
}
<nav class="bg-white border-gray-200 px-2 sm:px-4 py-2.5 rounded fixed top-0 z-10 w-full">
  <div class="container flex flex-wrap items-center justify-between mx-auto">
    <h2 class="font-press-start"><Strong>B</Strong>ank<strong>O</strong>fu</h2>
  <div class="flex md:order-2">

