import { Component } from '@angular/core';
import { CustomersService } from '../customers.service.';
import { Customer } from '../models/customer.model';

@Component({
  selector: 'app-dashboard-route',
  templateUrl: './dashboard-route.component.html',
  styleUrls: ['./dashboard-route.component.css']
})
export class DashboardRouteComponent {
  NumberOfCustmers=0;
  TotaleBalance=0;
  cusromers:Customer[]=[];

  constructor(private customersService: CustomersService) { }

  ngOnInit():void{
    this.customersService.getCustomers().subscribe(item=>{
      this.cusromers=item;
      this.NumberOfCustmers=item.length;
      this.TotaleBalance=this.cusromers.reduce((sum,item)=>(sum+Number.parseFloat(item.balance.toString())),0)
    })
  }
}
<div class="flex flex-col  bg-slate-500 md:px-44 md:py-36 p-8">
  <h2 class="text-center">
    The number and amount of bank customers can vary depending on factors such as the size of the bank, the types of services offered, and the demographics of the customer base. The number of customers refers to the total count of individuals or businesses with accounts at a bank, while the amount of customers refers to the total value of funds held in these accounts. These metrics are important indicators of a bank's overall success and profitability, and can vary widely between different types of banks and customer bases.
  </h2>
  <div class="flex  justify-center items-center md:grid-cols-2 xl:grid-cols-2 gap-6 m-14 flex-wrap ">
  <div class="w-full max-w-sm mx-auto bg-white border cursor-pointer hover:scale-95 flex justify-center items-center text-center font-bold border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
    <div class="items-center justify-center  py-10">

      <div><h1>Our Customers</h1></div>
      <hr>
      <div class="mt-3">
        {{NumberOfCustmers}}
      </div>
      <div class="text-gray-400">
        <i>loyal customer</i>

      </div>
      <span class="material-symbols-outlined text-blue-400 hover:bg-blue-600 hover:text-white relative top-0 rounded-full">
        verified
        </span>
    </div>

  </div>
  <div  class="w-full max-w-sm mx-auto bg-white border cursor-pointer hover:scale-95 flex justify-center items-center font-bold text-center border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
    <div class=" items-center  p-10">

      <div>Total Balance</div>
      <hr>
      <div class="mt-3">{{TotaleBalance |currency }}</div>
    </div>

  </div>

  </div>
</div>
